mapper.select:
SELECT 

FROM dev_write_off a

WHERE a.id = #{id}

SELECT 

FROM dev_write_off a




		AND a.projectname = #{projectname}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}

SELECT 

FROM dev_write_off a






		ORDER BY ${page.orderBy}
