mapper.select:
SELECT 

FROM pj_value_details a

WHERE a.id = #{id}

SELECT
FROM pj_value_details a
AND a.cate_id = #{cateId}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM pj_value_details a






		ORDER BY ${page.orderBy}

SELECT a.* FROM pj_value_details a WHERE a.cate_id in
#{cateId}
and a.id not in
#{detailId}
ORDER BY cate_id ASC
